script:
function updateDateTime() {
  // Select elements within the workspace block
  let dateElem = root_element.querySelector('.date-display');
  let timeElem = root_element.querySelector('.time-display');
  if (!dateElem || !timeElem) return;

  let now = new Date();

  // Format date as MM/DD/YYYY
  let dateStr = now.toLocaleDateString('en-US');
  // Format time as HH:MM AM/PM
  let timeStr = now.toLocaleTimeString('en-US', { hour: '2-digit', minute:'2-digit' });

  dateElem.textContent = dateStr;
  timeElem.textContent = timeStr;
}

// Initial call
updateDateTime();
// Update every second
setInterval(updateDateTime, 1000);

style:
.frappe-datetime-block {
  display: flex;
  flex-direction: column;
  align-items: end;
  padding: 12px 0;
}
.frappe-datetime-block .date-display {
  font-size: 1.5rem;
  font-weight: bold;
  color: #2563eb; /* Tailwind blue-600 */
  text-align: center;
  margin-bottom: 2px;
}
.frappe-datetime-block .time-display {
  font-size: 0.9rem;
  color: #6b7280; /* Tailwind gray-500 */
  text-align: center;
  font-weight: 500;
}

.quick-actions-card {
  padding: 16px;
  background: #fff;
  border-radius: 8px;
  box-shadow: 0 1px 4px rgb(0 0 0 / 0.1);
}

.quick-actions-title {
  font-weight: 700;
  font-size: 1.25rem;
  margin-bottom: 12px;
  color: #1e293b; /* Dark slate for title */
}

.quick-actions-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
  gap: 16px;
}

.quick-action-btn {
  background: #f9fafb;
  border-radius: 8px;
  padding: 20px 16px;
  border: none;
  cursor: pointer;
  text-align: left;
  transition: background-color 0.3s ease;
  color: #1e293b; /* dark slate text */
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.quick-action-btn:hover {
  background: #e0e7ff; /* light blue hover */
}

.quick-action-btn:focus,
.quick-action-btn.active {
  background: #2563eb; /* Bright blue for active */
  color: white;
}

.quick-action-btn svg {
  stroke: currentColor;
  width: 36px;
  height: 36px;
  flex-shrink: 0;
}

.quick-action-btn h3.quick-action-title {
  font-weight: 600;
  font-size: 1.1rem;
  margin: 0;
}

.quick-action-btn p.quick-action-desc {
  font-size: 0.875rem;
  font-weight: 400;
  margin: 0;
  opacity: 0.8;
}

/* Individual button colors */

.quick-action-btn.blue {
  background: #2563eb;
  color: white;
}

.quick-action-btn.green {
  background: #22c55e; /* Emerald 500 */
  color: white;
}

.quick-action-btn.purple {
  background: #a855f7; /* Purple 500 */
  color: white;
}

.quick-action-btn.orange {
  background: #f97316; /* Orange 500 */
  color: white;
}
